	// 	}
	// });

	// 説明書を表示する
	// feeles.openReadme('stages/1/README.md');

	// プレイヤー（騎士）
	const player = (Hack.player = new Player(('▼ スキン', _kきし)))
	player.mod(('▼ スキン', _kきし))
	// プレイヤーを　3,　5 の位置に移動する
	player.locate(3, 5)
	// プレイヤーの体力
	player.hp = 3
	// プレイヤーの攻撃力
	player.atk = 1
	// プレイヤーがやられたら...
	player.onたおれたとき = function() {
		// プレイヤーを削除
		this.destroy()
