	// スライムの体力
	item1.hp = 3
	// スライムの攻撃力
	item1.atk = 1
	// スライムを 9, 5 の位置に移動する ( map1 )
	item1.locate(9, 5, 'map1')
	// スライムが攻撃されたら
	item1.on('attacked', () => {
		// 1.4秒後に...
		setTimeout(() => {
			if (item1.behavior === BehaviorTypes.Idle) {
				// 反撃する！（攻撃する）
				item1.attack()
			}
		}, 1400)
		// 1400 は、 1.4 秒のこと
	})

	// 階段
	const item2 = new RPGObject()
	item2.mod(('▼ スキン', _kくだりかいだん))
	// 階段を 12, 5 の位置に移動 ( map1 )
	item2.locate(12, 5, 'map1')
	// 階段にプレイヤーが乗ったら...
	item2.onふまれた = event => {
		if (event.item === Hack.player) {
			// 次のステージに！
			feeles.replace('stages/3/index.html')
		}
	}

	// そうさせつめい (1)
	Hack.logAtPoint('やじるしキーを おして\nキャラクターを うごしてみよう', 3, 5)

	// そうさせつめい (2)
	let attacked = false
	player.once('becomeattack', () => {
		attacked = true
	})
	Hack.logFunc(next =>
		attacked
			? next()
			: `
スペースキーを おして
こうげき してみよう`
	)

	// そうさせつめい (3)
	Hack.logFunc(next =>
		item1.hp >= 3
			? `
スライムに ちかづいて
こうげき してみよう！`
			: next()
	)

	// そうさせつめい (4)
	item1.once('becomedead', () => {
		Hack.logFunc(`
スライムは たおれた！
かいだんを おりよう`)
	})

	// このステージを改造
	extra(6, 9, 'map1', 'stages/1/main.js')
}

game.onload = gameStart
Hack.on('load', createMap)
Hack.start()

