import { gameclear, prepareUtils } from '../../utils'
import extra from '../extra'
import createMap from './maps'

register(window)
prepareUtils()

function gameStart() {
	game.dispatchEvent(new enchant.Event('awake'))

	// map1 を読み込む
	Hack.changeMap('map1')

	// コードをとじる
	feeles.closeCode()

	// 解説の youtube を開く
