import { enchant, Hack, register } from 'http://localhost:8080/main.js'
import createMap from './maps'
import extra, { flag } from '../extra'
import { prepareUtils } from '../../utils'

register(window)
prepareUtils()

function gameStart() {
	game.dispatchEvent(new enchant.Event('awake'))

	// map1 を読み込む
	Hack.changeMap('map1')

	// プレイヤー（騎士）
	const player = (Hack.player = new Player(('▼ スキン', _kきし)))
	player.mod(('▼ スキン', _kきし))
	// プレイヤーを 7, 5 の位置に移動する
	player.locate(7, 5)
	// プレイヤーの体力
	player.hp = 3
	// プレイヤーの攻撃力
	player.atk = 1
	// プレイヤーがやられたら...
	player.onたおれたとき = function() {
		// プレイヤーを削除する
		this.destroy()
		// ゲームオーバー
		Hack.gameover()
	}

	// 神官
	const boy = new RPGObject()
	boy.mod(('▼ スキン', _o男の子))
	// 神官を 7, ３ の位置に移動する
	boy.locate(7, 3)
	// フラグが立っていたら...
	if (flag) {
		Hack.log('転移装置 の ふういんが とけた!')
		// 神官にぶつかったら...
		boy.onぶつかった = () => {
			Hack.log(
				'きみの そのちから、このせかいをもっと 自由にできるんじゃないかな'
			)
		}
	}
	// フラグが立っていなかったら...
	else {
		// 神官にぶつかったら...
		boy.onぶつかった = () => {
			Hack.log('きみが ふういんを といてくれるのかい？')
			// 魔道書を開く
			feeles.openEditor('stages/extra.js')
		}
	}

	// 転移装置

	// 4, 6 の位置に　明るい緑色（lightgreen） の転移装置を作る
	const warp1 = createWarp(
		4,
		6,
		'lightgreen',
		'ステージ１ "はじまりの森" に ワープしますか？',
		'stages/1/index.html'
	)

	// 5, 7 の位置に　オレンジ色（orange） の転移装置を作る
	const warp2 = createWarp(
		5,
