	// (ÂæåÊñπ‰∫íÊèõÊÄß) hackforplay.xyz ÁßªÊ§ç„Éê„Éº„Ç∏„Éß„É≥
	gameOnLoad = gameFunc.gameOnLoad
	hackOnLoad = gameFunc.hackOnLoad
} else {
	// „Åµ„Å§„ÅÜ„ÅØ„Åì„Å°„Çâ
	gameOnLoad = gameFunc
	hackOnLoad = maps
}

// „Ç≤„Éº„É†„Çí„Å§„Åè„Çã
game.onload = async () => {
	// gameOnLoad „Çà„ÇäÂÖà„Å´ÂÆüË°å„Åô„Çã„Ç§„Éô„É≥„Éà
	// lifelabel „Å™„Å©„Åå gameOnLoad ÊôÇ„Å´ÂèÇÁÖß„Åß„Åç„Å™„ÅÑÂØæÁ≠ñ
	game.dispatchEvent(new enchant.Event('awake'))

	await gameOnLoad()

	// Hack.player „Åå„Å™„ÅÑ„Å®„Åç window.player „Çí‰ª£„Çè„Çä„Å´ÂÖ•„Çå„Çã
	if (window.player && !Hack.player) {
		Hack.player = window.player
	}

	// update Èñ¢Êï∞„ÇíÈñãÂßã
	game.on('enterframe', update)

	// „Ç≤„Éº„É†„ÇØ„É™„Ç¢„ÅÆÊ∏¨ÂÆöÔºàhackforplay.xyz „ÅÆÊñ∞‰ªïÊßòÔºâ
	Hack.on('gameclear', () => {
		if (feeles.dispatchOnMessage) {
			feeles.dispatchOnMessage({
				labelName: 'gameclear',
				labelValue: 'gameclear'
			})
		}
	})
}

