  - label: ルーレット
    code: |
      // ここからルーレット
      const item1 = new RPGObject(('▼ スキン', Skin.ルビー));
      item1.locate(11, 3, 'map1');
      item1.うごいている = ('▼ フラグ', false);
      item1.on(('▼ イベント', 'こうげきされた'), () => {
        // うごいているかどうかを逆にする
        item1.うごいているか = !item1.うごいているか;
        if (item1.うごいているか) {
          // ルーレット開始
          item1.start(item1.ルーレットプログラム);
        }
      });
      // ルーレットがストップしたとき
      item1.on('ストップ', (event) => {
        const スキン = event.スキン;
        // スコアのプログラム
        if (スキン === ('▼ スキン', Skin.ルビー)) {
          Hack.score += 1; // rubyなら、SCORE + 1
        }
        if (スキン === ('▼ スキン', Skin.ドクロ)) {
          Hack.score -= 1; // skullなら、SCORE - 1
        }
      });
      item1.ルーレットプログラム = async(self, count, next) => {
        await self.wait(0.1);
        const スキン = random([
          ('▼ スキン', Skin.ルビー),
          ('▼ スキン', Skin.ドクロ),
          ('▼ スキン', Skin.ウンコ)
        ]);
        self.mod(スキン);
        if (!item1.うごいているか) {
          // 止めたいとき
          const event = new Event('ストップ');
          event.スキン = スキン;
          self.dispatchEvent(event);
          // ストップ
          return;
        }
        // くりかえし
        next();
      };
      // ここまでルーレット
